const Home = ({ onNavigate, dark, onToggleMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [openKeys, setOpenKeys] = useState(() => new Set());
  const [sidebarHidden, setSidebarHidden] = useState(false);

  function toggleDetails(key) {
    setOpenKeys(prev => {
      const next = new Set(prev);
      if (next.has(key)) next.delete(key); else next.add(key);
      return next;
    });
  }

  // Listen to sidebar visibility from scroll tracking
  useEffect(() => {
    function checkSidebarState() {
      const sidebar = document.querySelector('aside.sidebar-drawer');
      if (sidebar) {
        const isHidden = sidebar.classList.contains('sidebar-hidden');
        setSidebarHidden(isHidden);
      }
    }

    window.addEventListener('scroll', checkSidebarState);
    return () => window.removeEventListener('scroll', checkSidebarState);
  }, []);

  return (
    <div className="min-h-screen w-screen flex">
      {/* Mobile: hamburger to open sidebar */}
      <div className="lg:hidden fixed top-0 left-0 right-0 z-20 px-4 py-2 bg-white dark-mode:bg-slate-900">
        <button onClick={() => setSidebarOpen(true)} className="px-3 py-2 rounded bg-indigo-600 text-white">â˜° Menu</button>
      </div>

      <div className="w-screen lg:grid lg:grid-cols-4 lg:min-h-screen flex flex-col lg:flex-row">
        <div className="col-span-1 hidden lg:block sticky top-0 h-screen overflow-y-auto">
          <Sidebar onNavigate={onNavigate} onLogout={() => onNavigate('login')} dark={dark} onToggleMode={onToggleMode} open={true} />
        </div>

        <div className={`col-span-3 w-full pt-14 lg:pt-0 px-4 py-8 home-content ${sidebarHidden ? 'sidebar-hidden-state' : 'sidebar-visible-state'}`}>
          <section className="mb-8">
            <div className="hero-banner rounded-xl p-12 shadow-2xl">
              <h1 className="text-5xl font-extrabold mb-4 leading-tight hero-title">Understanding Emotions â€” A Visual Guide</h1>
              <p className="text-lg opacity-90 max-w-3xl hero-text">Explore a rich palette of emotions, learn brief descriptions, and get quick practical suggestions. Click any card to expand details and suggestions. This interactive guide complements the real-time emotion detector.</p>
            </div>
          </section>

          <section className="emotions-section">
            <h2 className="text-3xl font-bold mb-4">Emotional Spectrum</h2>
            <div className="emotions-grid">
              {EMOTIONS.map((emo, idx) => {
                const delay = `${idx * 40}ms`;
                const opened = openKeys.has(emo.key);
                return (
                  <article key={emo.key} className={`emotion-card`} style={{ animationDelay: delay }} onClick={() => toggleDetails(emo.key)} role="button" tabIndex={0} onKeyDown={(e) => { if (e.key === 'Enter') toggleDetails(emo.key); }}>
                    <div className={`card-top bg-gradient-to-br ${emo.color}`}>
                      <div className="emoji">{emo.emoji}</div>
                      <div className="title">{emo.name}</div>
                    </div>
                    <div className="card-body">
                      <p className="desc">{emo.description}</p>
                      <div className={`card-details ${opened ? 'open' : ''}`}>
                        <h4 className="mt-3 font-semibold">Suggestions</h4>
                        <ul className="mt-2 suggestion-list">
                          {emo.suggestions.map((s, i) => <li key={i} className="suggestion">{s}</li>)}
                        </ul>
                      </div>
                    </div>
                  </article>
                );
              })}
            </div>
          </section>

          <section className="mt-12">
            <div className="p-8 rounded-lg bg-white/5 border shadow-inner">
              <h3 className="text-2xl font-bold mb-4">Explore more</h3>
              <p className="mb-6 opacity-90">Dive deeper into practical applications, ethical guidelines, and ways to contribute. Each card below links to quick resources to help you learn, build, and share responsibly.</p>

              <div className="feature-grid">
                {[
                  { id: 'usecases', title: 'Use Cases', desc: 'See real-world scenarios where emotion recognition adds value â€” accessibility, mental health, and adaptive UX.', cta: 'Explore', icon: 'search' },
                  { id: 'best-practices', title: 'Best Practices', desc: 'Guidelines on ethics, consent, and privacy to help you deploy responsibly.', cta: 'Guidelines', icon: 'doc' },
                  { id: 'contribute', title: 'Get Involved', desc: 'Contribute, report issues, or suggest datasets and models to improve the project.', cta: 'Contribute', icon: 'handshake' }
                ].map((f, i) => {
                  const delay = `${i * 80}ms`;
                  const openHandler = () => onNavigate ? onNavigate(f.id) : window.location.assign(`/${f.id}`);
                  return (
                    <div key={f.id} className="feature-card" role="button" tabIndex={0} onClick={openHandler} onKeyDown={(e)=>{ if(e.key==='Enter') openHandler(); }} style={{ animationDelay: delay }}>
                      <div className="feature-icon" aria-hidden>
                        {f.icon === 'search' && (
                          <svg width="22" height="22" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden>
                            <path d="M21 21l-4.35-4.35" stroke="#0f172a" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round" />
                            <circle cx="11" cy="11" r="6" stroke="#0f172a" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round" />
                          </svg>
                        )}
                        {f.icon === 'doc' && (
                          <svg width="22" height="22" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden>
                            <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z" stroke="#0f172a" strokeWidth="1.4" strokeLinecap="round" strokeLinejoin="round" />
                            <path d="M14 2v6h6" stroke="#0f172a" strokeWidth="1.4" strokeLinecap="round" strokeLinejoin="round" />
                          </svg>
                        )}
                        {f.icon === 'handshake' && (
                          <svg width="22" height="22" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden>
                            <path d="M21 12l-4.35 4.35a2 2 0 0 1-2.83 0L9 12" stroke="#0f172a" strokeWidth="1.4" strokeLinecap="round" strokeLinejoin="round" />
                            <path d="M3 7l3 3 4-4" stroke="#0f172a" strokeWidth="1.4" strokeLinecap="round" strokeLinejoin="round" />
                          </svg>
                        )}
                      </div>
                      <h4 className="feature-title">{f.title}</h4>
                      <p className="feature-desc">{f.desc}</p>
                      <div className="feature-cta">{f.cta}</div>
                    </div>
                  );
                })}
              </div>
            </div>
          </section>

          <footer className="app-footer mt-12">
            <div className="footer-inner p-8 rounded-lg bg-gradient-to-r from-slate-800 to-slate-900 text-white shadow-lg">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <h4 className="font-bold text-lg">EmotionLab</h4>
                  <p className="mt-2 opacity-90">Realtime emotion recognition demo â€” built for research and prototyping. Respect privacy and obtain consent.</p>
                </div>
                <div>
                  <h5 className="font-semibold">Resources</h5>
                  <ul className="mt-2 opacity-90">
                    <li>Docs</li>
                    <li>Privacy</li>
                    <li>Terms</li>
                  </ul>
                </div>
                <div>
                  <h5 className="font-semibold">Contact</h5>
                  <p className="mt-2 opacity-90">contact@emotionlab.example</p>
                </div>
              </div>
              <div className="mt-6 text-sm opacity-80">Â© {new Date().getFullYear()} EmotionLab â€” All rights reserved</div>
            </div>
          </footer>
        </div>
      </div>

      {/* Mobile Sidebar overlay */}
      <Sidebar onNavigate={onNavigate} onLogout={() => onNavigate('login')} dark={dark} onToggleMode={onToggleMode} open={sidebarOpen} onClose={() => setSidebarOpen(false)} isOverlay={true} />
    </div>
  );
};

export default Home;
