import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';
import './EmotionDetector.css';

const API_BASE_URL = 'http://localhost:5000';

const EmotionDetector = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isRunning, setIsRunning] = useState(false);
  const [emotions, setEmotions] = useState([]);
  const [stats, setStats] = useState({});
  const [emotionCounts, setEmotionCounts] = useState({});
  const [loading, setLoading] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('connecting');

  // Check backend connection
  useEffect(() => {
    const checkBackend = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/health`);
        setConnectionStatus(response.data.model_loaded ? 'connected' : 'model-error');
      } catch (error) {
        setConnectionStatus('disconnected');
      }
    };

    checkBackend();
    const interval = setInterval(checkBackend, 5000);
    return () => clearInterval(interval);
  }, []);

  // Initialize webcam
  useEffect(() => {
    const initWebcam = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: { width: 640, height: 480 }
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          // Ensure video is not mirrored so bounding boxes align with real face
          videoRef.current.style.transform = 'scaleX(1)';
        }
      } catch (error) {
        console.error('Error accessing webcam:', error);
        setConnectionStatus('webcam-error');
      }
    };

    if (isRunning) {
      initWebcam();
    }

    return () => {
      if (videoRef.current && videoRef.current.srcObject) {
        videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      }
    };
  }, [isRunning]);

  // Main detection loop
  useEffect(() => {
    if (!isRunning || !videoRef.current || !canvasRef.current) return;

    const detect = async () => {
      try {
        setLoading(true);

        // Capture frame from video
        const ctx = canvasRef.current.getContext('2d');
        // match canvas to video actual dimensions to avoid scaling issues
        const vw = videoRef.current.videoWidth || 640;
        const vh = videoRef.current.videoHeight || 480;
        canvasRef.current.width = vw;
        canvasRef.current.height = vh;
        ctx.clearRect(0, 0, vw, vh);
        ctx.drawImage(videoRef.current, 0, 0, vw, vh);

        // Convert to base64
        const imageData = canvasRef.current.toDataURL('image/jpeg');

        // Send to backend
        const response = await axios.post(`${API_BASE_URL}/api/detect-emotion`, {
          image: imageData
        });

        if (response.data.emotions) {
          setEmotions(response.data.emotions);
          
          // Update statistics
          const newCounts = { ...emotionCounts };
          response.data.emotions.forEach(result => {
            newCounts[result.emotion] = (newCounts[result.emotion] || 0) + 1;
          });
          setEmotionCounts(newCounts);

          // Calculate stats
          if (response.data.emotions.length > 0) {
            const avgConfidence = (
              response.data.emotions.reduce((sum, e) => sum + e.confidence, 0) /
              response.data.emotions.length
            ).toFixed(3);
            setStats({
              totalDetected: response.data.emotions.length,
              avgConfidence: avgConfidence,
              dominantEmotion: response.data.emotions[0].emotion
            });
          }
        }

        // Draw results on canvas (overlay)
        drawResults(ctx, response.data.emotions, { canvasW: canvasRef.current.width, canvasH: canvasRef.current.height });
        setLoading(false);

        // Continue detection
        setTimeout(detect, 500);
      } catch (error) {
        console.error('Detection error:', error);
        setLoading(false);
        setTimeout(detect, 1000);
      }
    };

    detect();
  }, [isRunning, emotionCounts]);

  const drawResults = (ctx, emotionsData, dims = {}) => {
    if (!emotionsData || emotionsData.length === 0) return;
    const { canvasW = 640, canvasH = 480 } = dims;

    emotionsData.forEach(emotion => {
      const color = getEmotionColor(emotion.emotion);

      const x = emotion.x;
      const y = emotion.y;
      const w = emotion.width;
      const h = emotion.height;

      // visible semi-transparent fill
      ctx.fillStyle = hexToRgba(color, 0.12);
      roundRect(ctx, x, y, w, h, 8, true, false);

      // outer stroke
      ctx.strokeStyle = color;
      ctx.lineWidth = Math.max(3, Math.round(Math.min(canvasW, canvasH) / 200));
      roundRect(ctx, x, y, w, h, 8, false, true);

      // label background
      ctx.fillStyle = color;
      ctx.font = '600 16px Inter, Arial';
      const label = `${emotion.emotion} ${(emotion.confidence * 100).toFixed(1)}%`;
      const textW = ctx.measureText(label).width + 10;
      ctx.fillRect(x, Math.max(0, y - 30), textW, 26);

      // label text
      ctx.fillStyle = '#ffffff';
      ctx.fillText(label, x + 6, Math.max(16, y - 12));
    });
  };

  // Helper to draw rounded rect
  const roundRect = (ctx, x, y, width, height, radius, fill, stroke) => {
    if (typeof radius === 'undefined') radius = 5;
    ctx.beginPath();
    ctx.moveTo(x + radius, y);
    ctx.arcTo(x + width, y, x + width, y + height, radius);
    ctx.arcTo(x + width, y + height, x, y + height, radius);
    ctx.arcTo(x, y + height, x, y, radius);
    ctx.arcTo(x, y, x + width, y, radius);
    ctx.closePath();
    if (fill) ctx.fill();
    if (stroke) ctx.stroke();
  };

  const hexToRgba = (hex, alpha = 1) => {
    const c = hex.replace('#', '');
    const bigint = parseInt(c, 16);
    const r = (bigint >> 16) & 255;
    const g = (bigint >> 8) & 255;
    const b = bigint & 255;
    return `rgba(${r}, ${g}, ${b}, ${alpha})`;
  };

  const getEmotionColor = (emotion) => {
    const colors = {
      angry: '#FF6B6B',
      disgust: '#95E1D3',
      fear: '#FFE66D',
      happy: '#4ECDC4',
      neutral: '#95A5A6',
      sad: '#3498DB',
      surprise: '#E74C3C'
    };
    return colors[emotion] || '#FFFFFF';
  };

  const toggleDetection = () => {
    setIsRunning(!isRunning);
  };

  const resetStats = () => {
    setEmotionCounts({});
    setStats({});
    setEmotions([]);
  };

  const [finalizedEmotion, setFinalizedEmotion] = useState(null);
  const [suggestions, setSuggestions] = useState([]);

  const finalizeEmotion = () => {
    // choose dominant from stats or top frequency
    let chosen = stats.dominantEmotion || Object.keys(emotionCounts)[0] || null;
    if (!chosen && emotions.length > 0) chosen = emotions[0].emotion;
    setFinalizedEmotion(chosen);

    const map = {
      happy: [
        { title: 'Comedy Movie Night', desc: 'Keep the good vibes ‚Äî watch a light comedy.' },
        { title: 'Dance Playlist', desc: 'Uplifting tracks to keep you moving.' }
      ],
      sad: [
        { title: 'Comfort Music', desc: 'Soothing acoustic tracks.' },
        { title: 'Feel-Good Documentary', desc: 'Stories that uplift and inspire.' }
      ],
      angry: [
        { title: 'High-Energy Workout', desc: 'Channel energy into motion.' },
        { title: 'Meditation Session', desc: 'Short guided breathing.' }
      ],
      surprise: [
        { title: 'Thriller Short', desc: 'Quick suspenseful clip.' },
        { title: 'Puzzle Game', desc: 'Engaging brain teaser.' }
      ],
      fear: [
        { title: 'Relaxing Nature Sounds', desc: 'Calming audio for grounding.' },
        { title: 'Guided Breath', desc: '2-minute grounding exercise.' }
      ],
      disgust: [
        { title: 'Comedy Sketches', desc: 'Light content to shift mood.' },
        { title: 'Cooking Show', desc: 'Satisfying and wholesome.' }
      ],
      neutral: [
        { title: 'Popular Series', desc: 'Start a trending show episode.' },
        { title: 'Explore New Music', desc: 'Discover something new.' }
      ]
    };

    setSuggestions(map[chosen] || []);
  };

  return (
    <div className="emotion-detector">
      <div className="header">
        <h1>üé≠ Facial Emotion Recognition</h1>
        <div className={`status-indicator ${connectionStatus}`}>
          {connectionStatus === 'connected' ? '‚úì Connected' : '‚úó ' + connectionStatus}
        </div>
      </div>

      <div className="main-container">
        <div className="video-container">
          <video
            ref={videoRef}
            autoPlay
            muted
            className="video-feed"
            style={{ display: isRunning ? 'block' : 'none' }}
          />
          <canvas
            ref={canvasRef}
            width={640}
            height={480}
            className="canvas-overlay"
            style={{ display: isRunning ? 'block' : 'none' }}
          />
          {!isRunning && (
            <div className="start-prompt">
              Click "Start Detection" to begin
            </div>
          )}
        </div>

        <div className="control-panel">
          <button
            onClick={toggleDetection}
            className={`btn btn-primary ${isRunning ? 'btn-stop' : ''}`}
            disabled={connectionStatus === 'disconnected'}
          >
            {isRunning ? '‚èπ Stop Detection' : '‚ñ∂ Start Detection'}
          </button>

          <button
            onClick={resetStats}
            className="btn btn-secondary"
            disabled={!isRunning}
          >
            Reset Statistics
          </button>

          <div className="stats">
            <h3>Current Statistics</h3>
            {Object.keys(stats).length > 0 ? (
              <>
                <p><strong>Faces Detected:</strong> {stats.totalDetected}</p>
                <p><strong>Avg Confidence:</strong> {stats.avgConfidence}</p>
                <p><strong>Dominant Emotion:</strong> {stats.dominantEmotion}</p>
              </>
            ) : (
              <p className="placeholder">No detection data yet</p>
            )}
          </div>

          <div className="emotion-counts">
            <h3>Emotion Frequency</h3>
            {Object.keys(emotionCounts).length > 0 ? (
              <div className="counts-list">
                {Object.entries(emotionCounts)
                  .sort((a, b) => b[1] - a[1])
                  .map(([emotion, count]) => (
                    <div key={emotion} className="count-item">
                      <span className="emotion-label">{emotion}</span>
                      <div className="count-bar">
                        <div
                          className="count-fill"
                          style={{
                            width: `${(count / Math.max(...Object.values(emotionCounts))) * 100}%`,
                            backgroundColor: getEmotionColor(emotion)
                          }}
                        />
                      </div>
                      <span className="count-value">{count}</span>
                    </div>
                  ))}
              </div>
            ) : (
              <p className="placeholder">No emotion data yet</p>
            )}
          </div>
        </div>
      </div>

      {loading && <div className="loading-spinner" />}
      <div className="finalize-panel" style={{maxWidth: 1400, margin: '28px auto 0'}}>
        <div style={{display: 'flex', gap: 12, alignItems: 'center'}}>
          <button onClick={finalizeEmotion} className="btn btn-primary">üìå Capture & Finalize Emotion</button>
          <button onClick={() => { setFinalizedEmotion(null); setSuggestions([]); }} className="btn btn-secondary">Clear Final</button>
          {finalizedEmotion && <div style={{marginLeft: 12}}><strong>Finalized:</strong> {finalizedEmotion}</div>}
        </div>

        {suggestions && suggestions.length > 0 && (
          <div className="suggestions" style={{marginTop: 18, display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: 12}}>
            {suggestions.map((s, i) => (
              <div key={i} className="card" style={{padding: 14, borderRadius: 10, background: 'rgba(255,255,255,0.06)'}}>
                <h4 style={{margin: '0 0 6px'}}>{s.title}</h4>
                <p style={{margin: 0, opacity: 0.9}}>{s.desc}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default EmotionDetector;
