import React, { useState } from 'react';
import Sidebar from '../components/Sidebar';
import EmotionDetector from '../components/EmotionDetector';

const Dashboard = ({ onNavigate, dark, onToggleMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleLogout = () => onNavigate('home');

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-sky-50 via-indigo-50 to-fuchsia-50 px-4 py-6 md:py-10 font-[Poppins]">
      <div className="max-w-6xl mx-auto">

        {/* Top header */}
        <header className="flex items-center justify-between mb-6 md:mb-8">
          <div>
            <h1
              className="text-2xl md:text-3xl font-extrabold tracking-tight text-slate-900"
              style={{ fontFamily: "'Playfair Display', serif" }}
            >
              EmotionLab{' '}
              <span className="bg-clip-text text-transparent bg-gradient-to-r from-teal-500 via-sky-500 to-indigo-500">
                Dashboard
              </span>
            </h1>
            <p className="mt-1 text-xs md:text-sm text-slate-500">
              Monitor live emotions, test models, and explore real-time detection.
            </p>
          </div>

          {/* Mobile menu button */}
          <div className="lg:hidden flex items-center gap-3">
            <button
              type="button"
              onClick={() => setSidebarOpen(true)}
              className="inline-flex items-center gap-2 px-3 py-2 rounded-xl bg-indigo-600 text-white text-sm font-semibold shadow-md hover:bg-indigo-500 active:scale-95 transition-all"
            >
              <span className="text-lg">â˜°</span>
              <span className="hidden sm:inline">Menu</span>
            </button>
          </div>
        </header>

        {/* Main layout */}
        <div className="grid grid-cols-1 lg:grid-cols-[260px,1fr] gap-6 items-start">
          {/* Desktop sidebar */}
          <aside className="hidden lg:block">
            <Sidebar
              onNavigate={onNavigate}
              onLogout={handleLogout}
              dark={dark}
              onToggleMode={onToggleMode}
              open={true}
            />
          </aside>

          {/* Main content */}
          <main className="w-full">
            <div className="rounded-3xl bg-white/90 backdrop-blur-xl border border-slate-100 shadow-[0_20px_60px_rgba(15,23,42,0.12)] p-4 sm:p-6 md:p-7">
              {/* card header */}
              <div className="flex items-center justify-between gap-3 mb-4">
                <div>
                  <h2 className="text-lg md:text-2xl font-semibold text-slate-900">
                    Live Detection
                  </h2>
                  <p className="text-xs md:text-sm text-slate-500">
                    Allow camera access to start real-time emotion tracking and overlays.
                  </p>
                </div>
                <span className="hidden sm:inline-flex items-center gap-2 rounded-full bg-emerald-50 px-3 py-1 text-xs font-medium text-emerald-700 border border-emerald-100">
                  <span className="h-2 w-2 rounded-full bg-emerald-500 animate-pulse" />
                  Live
                </span>
              </div>

              {/* detector area */}
              <div className="rounded-2xl bg-slate-900/95 text-white p-3 sm:p-4 md:p-5 shadow-inner">
                <EmotionDetector />
              </div>

              {/* footer hint */}
              <p className="mt-3 text-[0.7rem] md:text-xs text-slate-500">
                Tip: good lighting and a centered face improve detection accuracy.
              </p>
            </div>
          </main>
        </div>
      </div>

      {/* Mobile / tablet overlay sidebar */}
      <Sidebar
        onNavigate={onNavigate}
        onLogout={handleLogout}
        dark={dark}
        onToggleMode={onToggleMode}
        open={sidebarOpen}
        onClose={() => setSidebarOpen(false)}
        isOverlay={true}
      />
    </div>
  );
};

export default Dashboard;
