// src/pages/Home.js
import React, { useState, useEffect, useRef } from "react";
import Sidebar from "../components/Sidebar";

// Updated EMOTIONS â€” now all emotion cards use brown â†’ red â†’ pink gradient
const EMOTIONS = [
  { key: "happy", name: "Happy", emoji: "ðŸ˜Š", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "A positive affective state associated with pleasure and contentment.",
    suggestions: ["Listen to upbeat music", "Share a smile", "Capture this moment"] },

  { key: "sad", name: "Sad", emoji: "ðŸ˜¢", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "A low mood state triggered by loss, disappointment, or introspection.",
    suggestions: ["Reach out to a friend", "Try soothing music", "Practice gentle breathing"] },

  { key: "angry", name: "Angry", emoji: "ðŸ˜ ", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "Heightened arousal with negative valence, often due to perceived wrongs.",
    suggestions: ["Take a walk", "Count to ten", "Try grounding exercises"] },

  { key: "surprised", name: "Surprised", emoji: "ðŸ˜®", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "A sudden astonishment that may be positive or negative.",
    suggestions: ["Pause and reflect", "Capture the moment", "Ask questions"] },

  { key: "fear", name: "Fear", emoji: "ðŸ˜¨", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "An anticipatory reaction to threat with protective behavior.",
    suggestions: ["Slow your breathing", "Identify the trigger", "Seek support"] },

  { key: "disgust", name: "Disgust", emoji: "ðŸ¤¢", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "A strong aversion linked to contamination or rejection.",
    suggestions: ["Step away", "Reflect on boundaries", "Practice self-care"] },

  { key: "neutral", name: "Neutral", emoji: "ðŸ˜", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "A baseline state with no strong emotion.",
    suggestions: ["Stay mindful", "Take a short break", "Observe non-judgmentally"] },

  { key: "excited", name: "Excited", emoji: "ðŸ¤©", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "High energy with positive anticipation.",
    suggestions: ["Be creative", "Share excitement", "Plan next steps"] },

  { key: "anxious", name: "Anxious", emoji: "ðŸ˜°", color: "from-[#A0522D] via-[#E53935] to-[#EC407A]",
    description: "Worry and tension about future events.",
    suggestions: ["Grounding technique", "Limit stimulants", "Write thoughts"] },
];

// Feature Details
const FEATURE_DETAILS = {
  usecases: {
    heading: "Use Cases: Where EmotionLab Fits",
    intro:
      "Emotion recognition can unlock better experiences and safer interactions when used responsibly.",
    bullets: [
      "Accessibility â€” adapt UI for users who appear overwhelmed",
      "Mental health check-ins â€” gentle nudges like breathing reminders",
      "Adaptive UX â€” change difficulty or visuals based on emotion",
      "Research â€” understand real emotional responses during testing",
    ],
    note: "Use emotion data only with explicit consent.",
  },

  "best-practices": {
    heading: "Best Practices",
    intro: "Emotions are personal data. Handle them with care.",
    bullets: [
      "Always ask for consent",
      "Never save raw video unless necessary",
      "Avoid high-stakes decisions",
      "Be transparent about limitations",
    ],
    note: "EmotionLab is a learning tool â€” not a judgment tool.",
  },

  contribute: {
    heading: "Get Involved",
    intro: "Help improve the project in many ways.",
    bullets: [
      "Report bugs",
      "Suggest UI improvements",
      "Test new models",
      "Help refine text and labels",
    ],
    note: "All feedback helps strengthen the project.",
  },
};

const Home = ({ onNavigate, dark, onToggleMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarHidden, setSidebarHidden] = useState(false);
  const [openKeys, setOpenKeys] = useState(() => new Set());
  const [activeFeature, setActiveFeature] = useState("usecases");
  const lastScrollY = useRef(0);

  function toggleDetails(key) {
    setOpenKeys((prev) => {
      const next = new Set(prev);
      next.has(key) ? next.delete(key) : next.add(key);
      return next;
    });
  }

  useEffect(() => {
    const handleScroll = () => {
      if (window.innerWidth < 1024) return;
      const current = window.scrollY;
      const delta = current - lastScrollY.current;
      if (delta > 10 && !sidebarHidden) setSidebarHidden(true);
      else if (delta < -10 && sidebarHidden) setSidebarHidden(false);
      lastScrollY.current = current;
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [sidebarHidden]);

  const detail = FEATURE_DETAILS[activeFeature];

  return (
    <div className="min-h-screen w-full bg-slate-50 dark:bg-slate-950 flex overflow-hidden">

      {/* MAIN CONTENT */}
      <main className="flex-1 px-4 pb-10 pt-16 overflow-y-auto">

        {/* HERO */}
        <section className="mb-8">
          <div className="rounded-xl p-12 shadow-2xl bg-gradient-to-r from-purple-600 via-pink-500 to-rose-500">
            <h1 className="text-5xl font-extrabold mb-4 text-white">
              Understanding Emotions â€” A Visual Guide
            </h1>
            <p className="text-lg text-white/90 max-w-3xl">
              Explore a rich palette of emotions, learn descriptions, and get practical suggestions.
            </p>
          </div>
        </section>

        {/* EMOTIONAL SPECTRUM */}
        <section>
          <h2 className="text-3xl font-bold mb-4 text-[#8B4513]">Emotional Spectrum</h2>

          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {EMOTIONS.map((emo) => {
              const opened = openKeys.has(emo.key);
              return (
                <article
                  key={emo.key}
                  className="rounded-xl shadow-lg bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-700"
                  onClick={() => toggleDetails(emo.key)}
                >
                  <div className={`p-4 rounded-t-xl text-white bg-gradient-to-r ${emo.color}`}>
                    <div className="text-4xl">{emo.emoji}</div>
                    <h3 className="font-bold text-xl mt-2">{emo.name}</h3>
                  </div>

                  <div className="p-4">
                    <p className="text-slate-700 dark:text-slate-300">{emo.description}</p>

                    {opened && (
                      <div className="mt-3">
                        <h4 className="font-semibold text-[#A0522D]">Suggestions</h4>
                        <ul className="mt-1 text-slate-600 dark:text-slate-300 list-disc ml-5">
                          {emo.suggestions.map((s, i) => (
                            <li key={i}>{s}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                </article>
              );
            })}
          </div>
        </section>

        {/* EXPLORE MORE */}
        <section className="mt-12">
          <h3 className="text-2xl font-bold mb-6">Explore More</h3>

          <div className="grid gap-6 md:grid-cols-3">
            {[
              {
                id: "usecases",
                title: "Use Cases",
                desc: "Real-world scenarios where emotions matter.",
                gradient: "from-green-400 via-lime-500 to-yellow-400",
              },
              {
                id: "best-practices",
                title: "Best Practices",
                desc: "Guidelines for safe and ethical usage.",
                gradient: "from-yellow-400 via-orange-500 to-amber-500",
              },
              {
                id: "contribute",
                title: "Get Involved",
                desc: "Help improve EmotionLab.",
                gradient: "from-orange-500 via-red-500 to-rose-500",
              },
            ].map((card) => (
              <div
                key={card.id}
                onClick={() => setActiveFeature(card.id)}
                className={`cursor-pointer p-6 rounded-2xl text-slate-900 shadow-xl bg-gradient-to-r ${card.gradient}`}
              >
                <h4 className="text-xl font-bold">{card.title}</h4>
                <p className="mt-2 opacity-90">{card.desc}</p>
              </div>
            ))}
          </div>

          {/* DETAILS PANEL */}
          <div className="mt-8 p-6 rounded-2xl bg-slate-100 dark:bg-slate-800 border shadow-lg">
            <h4 className="text-xl font-semibold text-[#8B4513]">{detail.heading}</h4>
            <p className="mt-2 text-slate-700 dark:text-slate-300">{detail.intro}</p>

            <ul className="mt-3 list-disc ml-5 text-slate-800 dark:text-slate-200">
              {detail.bullets.map((b, i) => (
                <li key={i}>{b}</li>
              ))}
            </ul>

            <p className="mt-3 italic text-[#A0522D]">{detail.note}</p>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Home;
