import React, {
  useRef,
  useEffect,
  useState,
  useCallback,
} from 'react';
import axios from 'axios';
import './EmotionDetector.css';

const API_BASE_URL = 'http://localhost:5000';

/* ----------------------------- Helper functions ----------------------------- */

function roundRect(ctx, x, y, width, height, radius = 5, fill, stroke) {
  ctx.beginPath();
  ctx.moveTo(x + radius, y);
  ctx.arcTo(x + width, y, x + width, y + height, radius);
  ctx.arcTo(x + width, y + height, x, y + height, radius);
  ctx.arcTo(x, y + height, x, y, radius);
  ctx.arcTo(x, y, x + width, y, radius);
  ctx.closePath();
  if (fill) ctx.fill();
  if (stroke) ctx.stroke();
}

function hexToRgba(hex, alpha = 1) {
  const c = hex.replace('#', '');
  const bigint = parseInt(c, 16);
  const r = (bigint >> 16) & 255;
  const g = (bigint >> 8) & 255;
  const b = bigint & 255;
  return `rgba(${r}, ${g}, ${b}, ${alpha})`;
}

function getEmotionColor(emotion) {
  const colors = {
    angry: '#FF6B6B',
    disgust: '#95E1D3',
    fear: '#FFE66D',
    happy: '#4ECDC4',
    neutral: '#95A5A6',
    sad: '#3498DB',
    surprise: '#E74C3C',
  };
  return colors[emotion] || '#FFFFFF';
}

/* ------------------------------ Main component ------------------------------ */

const EmotionDetector = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const [isRunning, setIsRunning] = useState(false);
  const [emotions, setEmotions] = useState([]);
  const [stats, setStats] = useState({});
  const [emotionCounts, setEmotionCounts] = useState({});
  const [connectionStatus, setConnectionStatus] = useState('connecting');

  const [finalizedEmotion, setFinalizedEmotion] = useState(null);
  const [suggestions, setSuggestions] = useState([]);

  /* ---------------------- Backend health check loop ----------------------- */

  useEffect(() => {
    const checkBackend = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/health`);
        setConnectionStatus(
          response.data.model_loaded ? 'connected' : 'model-error'
        );
      } catch (error) {
        setConnectionStatus('disconnected');
      }
    };

    checkBackend();
    const interval = setInterval(checkBackend, 5000);
    return () => clearInterval(interval);
  }, []);

  /* --------------------------- Webcam init/cleanup --------------------------- */

  useEffect(() => {
    let isCancelled = false;

    const setupWebcam = async () => {
      if (!isRunning) return;
      const videoElement = videoRef.current;
      if (!videoElement) return;

      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: { width: 640, height: 480 },
        });

        if (!isCancelled) {
          videoElement.srcObject = stream;
          // No transform here ‚Äì mirroring is handled by CSS on both video & canvas
        }
      } catch (err) {
        console.error('Error accessing webcam:', err);
        setConnectionStatus('webcam-error');
      }
    };

    setupWebcam();

    return () => {
      isCancelled = true;
      const videoElement = videoRef.current;
      if (videoElement && videoElement.srcObject) {
        videoElement.srcObject.getTracks().forEach((track) => track.stop());
      }
    };
  }, [isRunning]);

  /* --------------------------- Draw results helper --------------------------- */

  const drawResults = useCallback((ctx, emotionsData, dims = {}) => {
    if (!emotionsData || emotionsData.length === 0) return;

    const { canvasW = 640, canvasH = 480 } = dims;

    emotionsData.forEach((emotion) => {
      const color = getEmotionColor(emotion.emotion);

      const x = emotion.x;
      const y = emotion.y;
      const w = emotion.width;
      const h = emotion.height;

      // semi-transparent fill
      ctx.fillStyle = hexToRgba(color, 0.12);
      roundRect(ctx, x, y, w, h, 8, true, false);

      // border
      ctx.strokeStyle = color;
      ctx.lineWidth = Math.max(
        3,
        Math.round(Math.min(canvasW, canvasH) / 200)
      );
      roundRect(ctx, x, y, w, h, 8, false, true);

      // label background
      ctx.fillStyle = color;
      ctx.font = '600 16px Inter, Arial';
      const label = `${emotion.emotion} ${(emotion.confidence * 100).toFixed(
        1
      )}%`;
      const textW = ctx.measureText(label).width + 10;
      ctx.fillRect(x, Math.max(0, y - 30), textW, 26);

      // label text
      ctx.fillStyle = '#ffffff';
      ctx.fillText(label, x + 6, Math.max(16, y - 12));
    });
  }, []);

  /* -------------------------- Main detection loop --------------------------- */

  useEffect(() => {
    if (!isRunning) return;

    let cancelled = false;

    const detect = async () => {
      if (cancelled) return;

      if (!videoRef.current || !canvasRef.current) {
        if (!cancelled && isRunning) {
          setTimeout(detect, 200);
        }
        return;
      }

      const videoElement = videoRef.current;
      const canvasElement = canvasRef.current;

      try {
        const ctx = canvasElement.getContext('2d');

        const vw = videoElement.videoWidth || 640;
        const vh = videoElement.videoHeight || 480;

        // Set canvas size equal to video size
        canvasElement.width = vw;
        canvasElement.height = vh;

        // Draw full-res video frame
        ctx.clearRect(0, 0, vw, vh);
        ctx.drawImage(videoElement, 0, 0, vw, vh);

        // üîπ Use a smaller frame for backend to reduce lag
        const sendW = 320;
        const sendH = 240;
        const tempCanvas = document.createElement('canvas');
        tempCanvas.width = sendW;
        tempCanvas.height = sendH;
        const tctx = tempCanvas.getContext('2d');
        tctx.drawImage(videoElement, 0, 0, sendW, sendH);
        const imageData = tempCanvas.toDataURL('image/jpeg', 0.6);

        const response = await axios.post(
          `${API_BASE_URL}/api/detect-emotion`,
          { image: imageData }
        );

        // Raw detections are in 320x240 coordinate space
        const raw = response.data.emotions || [];

        // üîÅ Scale coordinates back to the full canvas size
        const scaleX = vw / sendW;
        const scaleY = vh / sendH;

        const detected = raw.map((e) => ({
          ...e,
          x: e.x * scaleX,
          y: e.y * scaleY,
          width: e.width * scaleX,
          height: e.height * scaleY,
        }));

        setEmotions(detected);

        if (detected.length > 0) {
          setEmotionCounts((prev) => {
            const next = { ...prev };
            detected.forEach((result) => {
              next[result.emotion] = (next[result.emotion] || 0) + 1;
            });
            return next;
          });

          const avgConfidence = (
            detected.reduce((sum, e) => sum + e.confidence, 0) /
            detected.length
          ).toFixed(3);

          setStats({
            totalDetected: detected.length,
            avgConfidence,
            dominantEmotion: detected[0].emotion,
          });
        }

        // draw boxes on the full-size canvas
        drawResults(ctx, detected, {
          canvasW: canvasElement.width,
          canvasH: canvasElement.height,
        });
      } catch (err) {
        console.error('Detection error:', err);
      } finally {
        if (!cancelled && isRunning) {
          // ~5‚Äì8 FPS ‚Äì adjust if you want faster/slower
          setTimeout(detect, 150);
        }
      }
    };

    detect();

    return () => {
      cancelled = true;
    };
  }, [isRunning, drawResults]);

  /* ------------------------------ UI handlers ------------------------------- */

  const toggleDetection = () => {
    setIsRunning((prev) => !prev);
    setFinalizedEmotion(null);
    setSuggestions([]);
  };

  const resetStats = () => {
    setEmotionCounts({});
    setStats({});
    setEmotions([]);
  };

  const finalizeEmotion = () => {
    let chosen =
      stats.dominantEmotion || Object.keys(emotionCounts)[0] || null;
    if (!chosen && emotions.length > 0) chosen = emotions[0].emotion;
    setFinalizedEmotion(chosen);

    const map = {
      happy: [
        {
          title: 'Comedy Movie Night',
          desc: 'Keep the good vibes ‚Äî watch a light comedy.',
        },
        { title: 'Dance Playlist', desc: 'Uplifting tracks to keep you moving.' },
      ],
      sad: [
        { title: 'Comfort Music', desc: 'Soothing acoustic tracks.' },
        {
          title: 'Feel-Good Documentary',
          desc: 'Stories that uplift and inspire.',
        },
      ],
      angry: [
        {
          title: 'High-Energy Workout',
          desc: 'Channel energy into motion.',
        },
        {
          title: 'Meditation Session',
          desc: 'Short guided breathing.',
        },
      ],
      surprise: [
        { title: 'Thriller Short', desc: 'Quick suspenseful clip.' },
        { title: 'Puzzle Game', desc: 'Engaging brain teaser.' },
      ],
      fear: [
        {
          title: 'Relaxing Nature Sounds',
          desc: 'Calming audio for grounding.',
        },
        {
          title: 'Guided Breath',
          desc: '2-minute grounding exercise.',
        },
      ],
      disgust: [
        {
          title: 'Comedy Sketches',
          desc: 'Light content to shift mood.',
        },
        { title: 'Cooking Show', desc: 'Satisfying and wholesome.' },
      ],
      neutral: [
        {
          title: 'Popular Series',
          desc: 'Start a trending show episode.',
        },
        {
          title: 'Explore New Music',
          desc: 'Discover something new.',
        },
      ],
    };

    setSuggestions(map[chosen] || []);
  };

  /* --------------------------------- JSX ------------------------------------ */

  return (
    <div className="emotion-detector">
      <div className="header">
        <h1>üé≠ Facial Emotion Recognition</h1>
        <div className={`status-indicator ${connectionStatus}`}>
          {connectionStatus === 'connected'
            ? '‚úì Connected'
            : '‚úó ' + connectionStatus}
        </div>
      </div>

      <div className="main-container">
        <div className="video-container">
          {/* Video is used as input only; canvas is what you see */}
          <video
            ref={videoRef}
            autoPlay
            muted
            className="video-feed"
            // you can hide it visually if you prefer:
            // style={{ display: 'none' }}
          />
          <canvas
            ref={canvasRef}
            width={640}
            height={480}
            className="canvas-overlay"
            style={{ display: isRunning ? 'block' : 'block' }}
          />
          {!isRunning && (
            <div className="start-prompt">
              Click &quot;Start Detection&quot; to begin
            </div>
          )}
        </div>

        <div className="control-panel">
          <button
            onClick={toggleDetection}
            className={`btn btn-primary ${isRunning ? 'btn-stop' : ''}`}
            disabled={connectionStatus === 'disconnected'}
          >
            {isRunning ? '‚èπ Stop Detection' : '‚ñ∂ Start Detection'}
          </button>

          <button
            onClick={resetStats}
            className="btn btn-secondary"
            disabled={!isRunning}
          >
            Reset Statistics
          </button>

          <div className="stats">
            <h3>Current Statistics</h3>
            {Object.keys(stats).length > 0 ? (
              <>
                <p>
                  <strong>Faces Detected:</strong> {stats.totalDetected}
                </p>
                <p>
                  <strong>Avg Confidence:</strong> {stats.avgConfidence}
                </p>
                <p>
                  <strong>Dominant Emotion:</strong> {stats.dominantEmotion}
                </p>
              </>
            ) : (
              <p className="placeholder">No detection data yet</p>
            )}
          </div>

          <div className="emotion-counts">
            <h3>Emotion Frequency</h3>
            {Object.keys(emotionCounts).length > 0 ? (
              <div className="counts-list">
                {Object.entries(emotionCounts)
                  .sort((a, b) => b[1] - a[1])
                  .map(([emotion, count]) => (
                    <div key={emotion} className="count-item">
                      <span className="emotion-label">{emotion}</span>
                      <div className="count-bar">
                        <div
                          className="count-fill"
                          style={{
                            width: `${
                              (count /
                                Math.max(
                                  ...Object.values(emotionCounts)
                                )) * 100
                            }%`,
                            backgroundColor: getEmotionColor(emotion),
                          }}
                        />
                      </div>
                      <span className="count-value">{count}</span>
                    </div>
                  ))}
              </div>
            ) : (
              <p className="placeholder">No emotion data yet</p>
            )}
          </div>
        </div>
      </div>

      <div
        className="finalize-panel"
        style={{ maxWidth: 1400, margin: '28px auto 0' }}
      >
        <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
          <button onClick={finalizeEmotion} className="btn btn-primary">
            üìå Capture &amp; Finalize Emotion
          </button>
          <button
            onClick={() => {
              setFinalizedEmotion(null);
              setSuggestions([]);
            }}
            className="btn btn-secondary"
          >
            Clear Final
          </button>
          {finalizedEmotion && (
            <div style={{ marginLeft: 12 }}>
              <strong>Finalized:</strong> {finalizedEmotion}
            </div>
          )}
        </div>

        {suggestions && suggestions.length > 0 && (
          <div
            className="suggestions"
            style={{
              marginTop: 18,
              display: 'grid',
              gridTemplateColumns:
                'repeat(auto-fit, minmax(220px, 1fr))',
              gap: 12,
            }}
          >
            {suggestions.map((s, i) => (
              <div
                key={i}
                className="card"
                style={{
                  padding: 14,
                  borderRadius: 10,
                  background: 'rgba(255,255,255,0.06)',
                }}
              >
                <h4 style={{ margin: '0 0 6px' }}>{s.title}</h4>
                <p style={{ margin: 0, opacity: 0.9 }}>{s.desc}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default EmotionDetector;
