import React, { useEffect, useRef, useState } from 'react';

const IconHome = ({ className = 'w-5 h-5' }) => (
  <svg aria-hidden="true" focusable="false" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
    <path d="M3 11.5L12 4l9 7.5" />
    <path d="M5 12v7a1 1 0 001 1h3v-5h6v5h3a1 1 0 001-1v-7" />
  </svg>
);

const IconCamera = ({ className = 'w-5 h-5' }) => (
  <svg aria-hidden="true" focusable="false" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
    <path d="M23 19a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2V7a2 2 0 0 1 2-2h3l2-3h6l2 3h3a2 2 0 0 1 2 2z" />
    <circle cx="12" cy="13" r="4" />
  </svg>
);

const IconSun = ({ className = 'w-5 h-5' }) => (
  <svg aria-hidden="true" focusable="false" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
    <circle cx="12" cy="12" r="4" />
    <path d="M12 2v2M12 20v2M4.93 4.93l1.41 1.41M17.66 17.66l1.41 1.41M2 12h2M20 12h2M4.93 19.07l1.41-1.41M17.66 6.34l1.41-1.41" />
  </svg>
);

const IconMoon = ({ className = 'w-5 h-5' }) => (
  <svg aria-hidden="true" focusable="false" className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
    <path d="M21 12.79A9 9 0 1111.21 3 7 7 0 0021 12.79z" />
  </svg>
);

const IconLogo = ({ className = 'w-10 h-10' }) => (
  <svg className={className} viewBox="0 0 48 48" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
    <defs>
      <linearGradient id="g1" x1="0" x2="1">
        <stop offset="0" stopColor="#7c3aed" />
        <stop offset="1" stopColor="#ec4899" />
      </linearGradient>
    </defs>
    <rect width="48" height="48" rx="10" fill="url(#g1)" />
    <circle cx="24" cy="20" r="8" fill="rgba(255,255,255,0.95)" />
    <path d="M18 30c1.5-2 4.5-2 6 0" stroke="white" strokeWidth="1.6" strokeLinecap="round" strokeLinejoin="round" opacity="0.9"/>
  </svg>
);

const Sidebar = ({ onNavigate, onLogout, dark = false, onToggleMode, open = true, onClose, isOverlay = false }) => {
  const containerRef = useRef(null);
  const [collapsed, setCollapsed] = useState(() => {
    try {
      return localStorage.getItem('sidebarCollapsed') === 'true';
    } catch (e) {
      return false;
    }
  });
  const [sidebarVisible, setSidebarVisible] = useState(true);
  const lastScrollY = useRef(0);

  useEffect(() => {
    function handleScroll() {
      const currentScrollY = window.scrollY;
      // Determine scroll delta: negative = scrolled up, positive = scrolled down
      const delta = currentScrollY - lastScrollY.current;
      // If user scrolled UP quickly (delta < -50), hide the sidebar so home can expand
      if (delta < -50) {
        setSidebarVisible(false);
      } else if (delta > 50) {
        // If user scrolled DOWN quickly (delta > 50), show the sidebar again
        setSidebarVisible(true);
      }
      lastScrollY.current = currentScrollY;
    }

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    try { localStorage.setItem('sidebarCollapsed', collapsed ? 'true' : 'false'); } catch (e) {}
  }, [collapsed]);

  useEffect(() => {
    if (!isOverlay) return; // only activate trap for overlay sidebar

    let previousActive = null;

    function getFocusableElements() {
      if (!containerRef.current) return [];
      const selector = 'a[href], button:not([disabled]), input, select, textarea, [tabindex]:not([tabindex="-1"])';
      return Array.from(containerRef.current.querySelectorAll(selector)).filter(el => el.offsetParent !== null);
    }

    function handleKeyDown(e) {
      if (!open) return;
      if (e.key === 'Escape') {
        e.preventDefault();
        onClose && onClose();
        return;
      }

      if (e.key === 'Tab') {
        const focusable = getFocusableElements();
        if (focusable.length === 0) return;
        const first = focusable[0];
        const last = focusable[focusable.length - 1];
        if (e.shiftKey) {
          if (document.activeElement === first) {
            e.preventDefault();
            last.focus();
          }
        } else {
          if (document.activeElement === last) {
            e.preventDefault();
            first.focus();
          }
        }
      }
    }

    if (open) {
      previousActive = document.activeElement;
      // focus the first focusable element inside sidebar, or the close button
      setTimeout(() => {
        const elems = getFocusableElements();
        if (elems.length) elems[0].focus();
      }, 0);
      document.addEventListener('keydown', handleKeyDown);
      // mark rest of page inert for screen readers
      document.querySelector('main')?.setAttribute('aria-hidden', 'true');
    }

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.querySelector('main')?.removeAttribute('aria-hidden');
      if (previousActive && previousActive.focus) previousActive.focus();
    };
  }, [open, isOverlay, onClose]);

  // sync collapsed state across tabs
  useEffect(() => {
    function onStorage(e) {
      if (e.key === 'sidebarCollapsed') {
        setCollapsed(e.newValue === 'true');
      }
    }
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  // Desktop: always show; Mobile: show as overlay when `open` is true
  return (
    <>
      {/* Overlay for small screens */}
      <div
        aria-hidden={!open}
        className={`fixed inset-0 bg-black/40 z-40 transition-opacity lg:hidden ${open ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'}`}
        onClick={onClose}
      />

      <aside
        ref={containerRef}
        role={isOverlay ? 'dialog' : 'complementary'}
        aria-label="Main navigation"
        aria-modal={isOverlay ? 'true' : undefined}
        onClick={() => {
          // If the sidebar is collapsed and user clicks it (desktop), expand it
          if (!isOverlay && collapsed) setCollapsed(false);
        }}
        className={`z-50 ${open ? 'translate-x-0' : '-translate-x-full'} transform top-0 left-0 ${collapsed && !isOverlay ? 'w-20' : 'w-64'} p-4 rounded-r-lg h-full fixed ${isOverlay ? 'lg:hidden' : 'lg:relative lg:translate-x-0 lg:w-auto lg:min-h-[70vh]'} transition-all sidebar-drawer bg-gradient-to-b from-indigo-600 via-purple-600 to-pink-500 text-white ${!isOverlay && !sidebarVisible ? 'sidebar-hidden' : 'sidebar-visible'}`}
        data-collapsed={collapsed && !isOverlay ? 'true' : 'false'}
        style={{ minHeight: '70vh' }}>
        <div className="flex items-center justify-between mb-4">
          {/* Brand block (clickable) */}
          <button
            type="button"
            onClick={(e) => { e.stopPropagation(); onNavigate && onNavigate('home'); onClose && onClose(); }}
            onKeyDown={(e) => { if (e.key === 'Enter' || e.key === ' ') { e.preventDefault(); onNavigate && onNavigate('home'); onClose && onClose(); } }}
            className="sidebar-brand-button sidebar-brand flex items-center gap-3 text-left"
            aria-label="Go to home"
          >
            <div className="logo-wrap" aria-hidden>
              <img src="/logo.svg" alt="EmotionLab logo" className="brand-logo" />
            </div>
            <div className="brand-text">
              <div className="brand-name">EmotionLab</div>
              <div className="brand-sub text-xs opacity-80">Realtime AI</div>
            </div>
          </button>
          {/* Collapse toggle for desktop */}
          <div>
            {!isOverlay && (
                <button
                  aria-label={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
                  aria-pressed={collapsed}
                  onClick={(e) => { e.stopPropagation(); setCollapsed(!collapsed); }}
                  className="hidden lg:inline-flex px-2 py-1 rounded bg-white/10 hover:bg-white/20 items-center"
                >
                  <svg className={`chev ${collapsed ? 'rotated' : ''}`} width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                    <path d="M7 17l7-5-7-5v10z" fill="currentColor" />
                  </svg>
                </button>
              )}
          </div>

          <div>
            <button aria-label="Close sidebar" className="lg:hidden px-2 py-1 rounded text-white" onClick={onClose}>âœ•</button>
          </div>
        </div>

        <nav className="flex flex-col gap-3">
          <button
            onClick={() => { onNavigate('home'); onClose && onClose(); }}
            className="app-sidebar-button text-left p-3 rounded-lg bg-transparent hover:bg-white/10 flex items-center gap-3"
            aria-label="Go to Home"
          >
            <IconHome />
            <span className="sidebar-label font-medium" aria-hidden={collapsed}>{'Home'}</span>
            <span className="tooltip-text">Home</span>
          </button>

          <button
            onClick={() => { onNavigate('dashboard'); onClose && onClose(); }}
            className="app-sidebar-button text-left p-3 rounded-lg bg-transparent hover:bg-white/10 flex items-center gap-3"
            aria-label="Open Dashboard"
          >
            <IconCamera />
            <span className="sidebar-label font-medium" aria-hidden={collapsed}>{'Dashboard'}</span>
            <span className="tooltip-text">Dashboard</span>
          </button>

          <button
            onClick={() => { onToggleMode && onToggleMode(); onClose && onClose(); }}
            title="Toggle theme"
            className="app-sidebar-button text-left p-3 rounded-lg bg-transparent hover:bg-white/10 flex items-center gap-3"
            aria-label="Toggle theme"
          >
            {dark ? <IconMoon /> : <IconSun />}
            <span className="sidebar-label sr-only md:not-sr-only font-medium" aria-hidden={collapsed}>{'Theme'}</span>
            <span className="tooltip-text">Theme</span>
          </button>
        </nav>

        <div className="mt-6">
          <button
            onClick={() => { onLogout && onLogout(); onClose && onClose(); }}
            className="sidebar-logout w-full px-3 py-2 bg-red-500 text-white rounded-lg"
            aria-label="Logout"
          >
            Logout
          </button>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
